const out = $('Run Daily Loop').first().json.stdout || '';
const stderr = $('Run Daily Loop').first().json.stderr || '';
const exitCode = $('Run Daily Loop').first().json.exitCode;
const allOk = exitCode === 0;

const now = new Date();
const lines = out.split('\n').filter(l => l.trim());
const taskLines = lines.filter(l => l.includes('タスク') || l.includes('完了') || l.includes('ERROR') || l.includes('スキップ'));

return [{ json: {
  date: now.toLocaleString('ja-JP', { timeZone: 'Asia/Tokyo' }),
  status: allOk ? 'success' : 'error',
  started_at: $('Schedule').first().json.timestamp || now.toISOString(),
  finished_at: now.toISOString(),
  steps: taskLines.slice(0, 10).map(l => ({
    name: l.replace(/^\[.*?\]\s*/, '').substring(0, 60),
    success: !l.toLowerCase().includes('error') && !l.toLowerCase().includes('fail'),
    output: l.substring(0, 200)
  })),
  error: allOk ? null : (stderr || out).substring(0, 300)
} }];
