const staticData = $getWorkflowStaticData('global');
const tasks = staticData.tasks || [];
const today = new Date().toISOString().split('T')[0];
const todayTasks = tasks.filter(t => t.created_at && t.created_at.startsWith(today));
return [{
  json: {
    generated_at: new Date().toISOString(),
    pipeline: {
      pending: tasks.filter(t => t.status === 'pending').length,
      running: tasks.filter(t => t.status === 'running').length,
      done: tasks.filter(t => t.status === 'done').length,
      failed: tasks.filter(t => t.status === 'failed').length
    },
    recent_tasks: tasks.slice(0, 20).map(t => ({
      id: t.id, type: t.type || 'blog_generate',
      title: t.title || t.slug || '-', slug: t.slug,
      status: t.status || 'done', priority: 'medium',
      created_at: t.created_at
    })),
    daily_limits: { '記事生成 (今日)': { used: todayTasks.length, limit: 10 } },
    page_locks: [], ab_tests: [], kpi_targets: [], page_freshness: []
  }
}];

const staticData = $getWorkflowStaticData('global');
if (!staticData.tasks) staticData.tasks = [];
const body = $input.first().json.body || $input.first().json;
staticData.tasks.unshift({
  id: Date.now().toString(),
  type: body.type || 'blog_generate',
  title: body.title || '',
  slug: body.slug || '',
  status: body.status || 'done',
  created_at: new Date().toISOString()
});
if (staticData.tasks.length > 100) staticData.tasks = staticData.tasks.slice(0, 100);
return [{ json: { ok: true, count: staticData.tasks.length } }];
