const fs = require('fs');
const path = '/home/node/.n8n/37design-tasks.jsonl';
const body = $input.first().json.body || $input.first().json;
const task = JSON.stringify({
  id: Date.now().toString(),
  type: body.type || 'blog_generate',
  title: body.title || '',
  slug: body.slug || '',
  status: body.status || 'done',
  created_at: new Date().toISOString()
});
fs.appendFileSync(path, task + '\n');
return [{ json: { ok: true } }];
